import React, { useState } from 'react';
import { GiphyFetch } from '@giphy/js-fetch-api';
import { Grid } from '@giphy/react-components';

const gf = new GiphyFetch('your_actual_giphy_api_key'); // Get API key from GIPHY

const GifPicker = ({ onSelect, onClose }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const fetchGifs = (offset) =>
    searchTerm
      ? gf.search(searchTerm, { offset, limit: 10 })
      : gf.trending({ offset, limit: 10 });

  return (
    <div className="absolute bottom-16 left-0 z-10 bg-white rounded-lg shadow-xl p-4 border w-[320px] h-[400px] overflow-y-auto">
      <input
        type="text"
        placeholder="Search GIFs..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="w-full px-3 py-2 border rounded-md mb-4"
      />
      <Grid
        width={280}
        columns={2}
        fetchGifs={fetchGifs}
        onGifClick={(gif) => {
          onSelect(`[gif:${gif.id}]`);
          onClose();
        }}
      />
    </div>
  );
};

export default GifPicker;
