import React, { useState, useEffect } from 'react';

const TENOR_API_KEY = 'your_tenor_api_key'; // Get from tenor.com/developer/dashboard

const GifPicker = ({ onSelect, onClose }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [gifs, setGifs] = useState([]);

  const fetchGifs = async (search = '') => {
    try {
      const endpoint = search
        ? `https://tenor.googleapis.com/v2/search?q=${search}&key=${TENOR_API_KEY}&limit=20`
        : `https://tenor.googleapis.com/v2/trending?key=${TENOR_API_KEY}&limit=20`;

      const response = await fetch(endpoint);
      const data = await response.json();
      setGifs(data.results);
    } catch (error) {
      console.error('Error fetching GIFs:', error);
    }
  };

  useEffect(() => {
    fetchGifs();
  }, []);

  return (
    <div className="absolute bottom-16 left-0 z-10 bg-white rounded-lg shadow-xl p-4 border w-[320px] h-[400px]">
      <div className="flex mb-4">
        <input
          type="text"
          placeholder="Search GIFs..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && fetchGifs(searchTerm)}
          className="flex-1 px-3 py-2 border rounded-l-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={() => fetchGifs(searchTerm)}
          className="px-4 py-2 bg-blue-500 text-white rounded-r-md hover:bg-blue-600"
        >
          Search
        </button>
      </div>
      <div className="grid grid-cols-2 gap-2 overflow-y-auto h-[calc(100%-60px)]">
        {gifs.map((gif) => (
          <button
            key={gif.id}
            onClick={() => {
              onSelect(`[gif:${gif.id}]`);
              onClose();
            }}
            className="p-1 hover:bg-gray-100 rounded transition-colors"
          >
            <img
              src={gif.media_formats.tinygif.url}
              alt={gif.content_description}
              className="w-full h-32 object-cover rounded"
            />
          </button>
        ))}
      </div>
    </div>
  );
};

export default GifPicker;
