import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const ContactList = ({ onSelectContact, selectedContact }) => {
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get(
          'http://localhost:5000/api/users/contacts',
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          }
        );
        setContacts(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching contacts:', error);
        setLoading(false);
      }
    };

    fetchContacts();
  }, []);

  if (loading) {
    return <div className="p-4">Loading contacts...</div>;
  }

  return (
    <div className="bg-white border-r border-gray-200">
      <div className="p-4 border-b border-gray-200">
        <h2 className="text-lg font-semibold">Contacts</h2>
      </div>
      <div className="overflow-y-auto h-[calc(100vh-64px)]">
        {contacts.map((contact) => (
          <div
            key={contact._id}
            onClick={() => onSelectContact(contact)}
            className={`p-4 cursor-pointer hover:bg-gray-50 flex items-center space-x-3 ${
              selectedContact?._id === contact._id ? 'bg-blue-50' : ''
            }`}
          >
            <div className="relative">
              <img
                src={contact.profilePicture || 'https://via.placeholder.com/40'}
                alt={contact.username}
                className="w-10 h-10 rounded-full"
              />
              <span
                className={`absolute bottom-0 right-0 w-3 h-3 rounded-full border-2 border-white ${
                  contact.isOnline ? 'bg-green-500' : 'bg-gray-400'
                }`}
              />
            </div>
            <div>
              <h3 className="font-medium">{contact.username}</h3>
              {contact.isOnline ? (
                <p className="text-sm text-green-500">Online</p>
              ) : (
                <p className="text-sm text-gray-500">Offline</p>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ContactList;
