import React, { useState } from 'react';
import EmojiPicker from 'emoji-picker-react';

const MessageInput = ({ onSend, disabled }) => {
  const [message, setMessage] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [showStickers, setShowStickers] = useState(false);

  const stickers = [
    {
      id: 1,
      url: 'https://media.giphy.com/media/v1.Y2lkPTc5MGI3NjExcDdtY2JrY3BxNXF1bXRzNXdrOGV1NnB4bWw5a3BveGptcnR6YnBpYiZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9cw/LOnt6uqjD9OexmQJRB/giphy.gif',
      label: 'Thumbs Up',
    },
    {
      id: 2,
      url: 'https://media.giphy.com/media/v1.Y2lkPTc5MGI3NjExbmh2ZmdvNnBpbTR0MjI4Z2VrYnJ3YTk4M3JvYmx1ZHV0YnB3NHB6eCZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9cw/dw36yjtOAtuSZyxEJG/giphy.gif',
      label: 'Hello',
    },
    // Add more stickers
  ];

  const onEmojiClick = (emojiObject) => {
    setMessage((prev) => prev + emojiObject.emoji);
  };

  const onStickerClick = (sticker) => {
    onSend(`[sticker:${sticker.id}]`);
    setShowStickers(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message.trim()) {
      onSend(message);
      setMessage('');
    }
  };

  return (
    <div className="relative">
      <form onSubmit={handleSubmit} className="p-4 bg-white border-t">
        <div className="flex items-center space-x-2">
          <div className="relative">
            <button
              type="button"
              className="p-2 text-gray-500 hover:text-gray-700 focus:outline-none"
              onClick={() => {
                setShowEmojiPicker(!showEmojiPicker);
                setShowStickers(false);
              }}
            >
              <span role="img" aria-label="emoji">
                😊
              </span>
            </button>
            {showEmojiPicker && (
              <div className="absolute bottom-12 left-0 z-10">
                <EmojiPicker onEmojiClick={onEmojiClick} />
              </div>
            )}
          </div>

          <button
            type="button"
            className="p-2 text-gray-500 hover:text-gray-700 focus:outline-none"
            onClick={() => {
              setShowStickers(!showStickers);
              setShowEmojiPicker(false);
            }}
          >
            <span role="img" aria-label="sticker">
              🎯
            </span>
          </button>

          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="flex-1 px-4 py-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            placeholder="Type a message..."
            disabled={disabled}
          />
          <button
            type="submit"
            disabled={!message.trim() || disabled}
            className={`px-6 py-2 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors duration-150 ${
              message.trim() && !disabled
                ? 'bg-blue-600 text-white hover:bg-blue-700'
                : 'bg-gray-300 text-gray-500 cursor-not-allowed'
            }`}
          >
            Send
          </button>
        </div>
      </form>

      {showStickers && (
        <div className="absolute bottom-16 left-0 z-10 bg-white rounded-lg shadow-xl p-4 border">
          <div className="grid grid-cols-4 gap-2">
            {stickers.map((sticker) => (
              <button
                key={sticker.id}
                onClick={() => onStickerClick(sticker)}
                className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <img
                  src={sticker.url}
                  alt={sticker.label}
                  className="w-16 h-16 object-contain"
                />
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default MessageInput;
