import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';
import ContactList from './ContactList';
import io from 'socket.io-client';
import axios from 'axios';
import LoadingSpinner from './LoadingSpinner';
import TypingIndicator from './TypingIndicator';
import config from '../config';
import Header from './Header';
import MessageInput from './MessageInput';

const Chat = () => {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [socket, setSocket] = useState(null);
  const [selectedContact, setSelectedContact] = useState(null);
  const messagesEndRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const typingTimeoutRef = useRef(null);
  const [sendingMessage, setSendingMessage] = useState(false);
  const [stickers, setStickers] = useState([]);

  useEffect(() => {
    const newSocket = io(config.SOCKET_URL);
    setSocket(newSocket);

    return () => newSocket.close();
  }, []);

  useEffect(() => {
    if (socket && user) {
      socket.emit('login', user.id);

      socket.on('receiveMessage', (message) => {
        if (
          selectedContact &&
          (message.sender === selectedContact._id || message.sender === user.id)
        ) {
          setMessages((prev) => [...prev, message]);
        }
      });
    }
  }, [socket, user, selectedContact]);

  const fetchMessages = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(
        `${config.API_URL}/api/messages/${selectedContact._id}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    } finally {
      setIsLoading(false);
    }
  }, [selectedContact]);

  useEffect(() => {
    if (selectedContact) {
      fetchMessages();
    }
  }, [selectedContact, fetchMessages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (newMessage.trim() && socket && selectedContact && !sendingMessage) {
      setSendingMessage(true);
      try {
        const response = await axios.post(
          `${config.API_URL}/api/messages`,
          {
            receiverId: selectedContact._id,
            content: newMessage,
          },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          }
        );

        socket.emit('sendMessage', {
          ...response.data,
          receiverId: selectedContact._id,
        });
        setMessages((prev) => [...prev, response.data]);
        setNewMessage('');
      } catch (error) {
        console.error('Error sending message:', error);
      } finally {
        setSendingMessage(false);
      }
    }
  };

  const handleTyping = () => {
    if (socket && selectedContact) {
      socket.emit('typing', { to: selectedContact._id });

      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }

      typingTimeoutRef.current = setTimeout(() => {
        socket.emit('stopTyping', { to: selectedContact._id });
      }, 1000);
    }
  };

  useEffect(() => {
    if (socket && user) {
      socket.on('userTyping', (userId) => {
        if (selectedContact?._id === userId) {
          setIsTyping(true);
        }
      });

      socket.on('userStoppedTyping', (userId) => {
        if (selectedContact?._id === userId) {
          setIsTyping(false);
        }
      });
    }
  }, [socket, user, selectedContact]);

  const groupMessagesByDate = (messages) => {
    const groups = {};
    messages.forEach((message) => {
      const date = new Date(message.createdAt).toLocaleDateString();
      if (!groups[date]) {
        groups[date] = [];
      }
      groups[date].push(message);
    });
    return groups;
  };

  const renderMessageContent = (content) => {
    if (content.startsWith('[sticker:')) {
      const stickerId = content.match(/\[sticker:(\d+)\]/)[1];
      const sticker = stickers.find((s) => s.id === parseInt(stickerId));
      return sticker ? (
        <img
          src={sticker.url}
          alt={sticker.label}
          className="w-32 h-32 object-contain"
        />
      ) : (
        content
      );
    }
    if (content.startsWith('[gif:')) {
      const gifUrl = content.match(/\[gif:(.*?)\]/)[1];
      return (
        <img
          src={gifUrl}
          alt="GIF"
          className="w-48 h-48 object-contain rounded-lg"
        />
      );
    }
    return content;
  };

  useEffect(() => {
    // Initialize stickers
    setStickers([
      {
        id: 1,
        url: 'https://media.giphy.com/media/LOnt6uqjD9OexmQJRB/giphy.gif',
        label: 'Thumbs Up',
      },
      {
        id: 2,
        url: 'https://media.giphy.com/media/dw36yjtOAtuSZyxEJG/giphy.gif',
        label: 'Hello',
      },
    ]);
  }, []); // Run once on mount

  return (
    <div className="h-screen flex flex-col">
      <Header />
      <div className="flex flex-1 overflow-hidden">
        <div className="w-1/4 border-r border-gray-200">
          <ContactList
            onSelectContact={setSelectedContact}
            selectedContact={selectedContact}
          />
        </div>
        <div className="flex-1 flex flex-col bg-gray-50">
          {selectedContact ? (
            <>
              <div className="p-4 border-b bg-white shadow-sm">
                <div className="flex items-center space-x-3">
                  <img
                    src={selectedContact.profilePicture}
                    alt={selectedContact.username}
                    className="w-10 h-10 rounded-full"
                  />
                  <div>
                    <h2 className="text-lg font-semibold text-gray-900">
                      {selectedContact.username}
                    </h2>
                    <p
                      className={`text-sm ${
                        selectedContact.isOnline
                          ? 'text-green-600'
                          : 'text-gray-500'
                      }`}
                    >
                      {selectedContact.isOnline ? 'Online' : 'Offline'}
                    </p>
                  </div>
                </div>
              </div>

              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {isLoading ? (
                  <LoadingSpinner />
                ) : (
                  <>
                    {isTyping && (
                      <div className="flex justify-start">
                        <TypingIndicator />
                      </div>
                    )}
                    <div ref={messagesEndRef} />
                    <div className="space-y-4">
                      {Object.entries(groupMessagesByDate(messages)).map(
                        ([date, msgs]) => (
                          <div key={date} className="space-y-4">
                            <div className="flex justify-center">
                              <span className="bg-gray-200 text-gray-600 text-xs px-2 py-1 rounded-full">
                                {date === new Date().toLocaleDateString()
                                  ? 'Today'
                                  : date}
                              </span>
                            </div>
                            {msgs.map((message, index) => (
                              <div
                                key={message._id || index}
                                className={`flex ${
                                  message.sender === user.id
                                    ? 'justify-end'
                                    : 'justify-start'
                                }`}
                              >
                                {message.sender !== user.id && (
                                  <img
                                    src={selectedContact.profilePicture}
                                    alt=""
                                    className="w-8 h-8 rounded-full mr-2 self-end"
                                  />
                                )}
                                <div
                                  className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                                    message.sender === user.id
                                      ? 'bg-blue-600 text-white rounded-br-none'
                                      : 'bg-white text-gray-800 rounded-bl-none shadow'
                                  }`}
                                >
                                  <div className="whitespace-pre-wrap break-words">
                                    {renderMessageContent(message.content)}
                                  </div>
                                  <div
                                    className={`text-xs mt-1 ${
                                      message.sender === user.id
                                        ? 'text-blue-100'
                                        : 'text-gray-500'
                                    }`}
                                  >
                                    {new Date(
                                      message.createdAt
                                    ).toLocaleTimeString([], {
                                      hour: '2-digit',
                                      minute: '2-digit',
                                    })}
                                    {message.sender === user.id && (
                                      <span className="ml-2">
                                        {message.read ? '✓✓' : '✓'}
                                      </span>
                                    )}
                                  </div>
                                </div>
                              </div>
                            ))}
                          </div>
                        )
                      )}
                    </div>
                  </>
                )}
              </div>

              <MessageInput
                onSend={(content) => {
                  const e = { preventDefault: () => {} };
                  setNewMessage(content);
                  sendMessage(e);
                }}
                disabled={!selectedContact || sendingMessage}
              />
            </>
          ) : (
            <div className="flex flex-col items-center justify-center h-full text-gray-500">
              <svg
                className="w-16 h-16 text-gray-400"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
